    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
            
        try{
           String UserName=Username.getText();
            String Password = pass.getText();
            Class.forName("com.mysql.cj.jdbc.Driver");
            Connection con=DriverManager.getConnection("jdbc:mysql://localhost:3306/Studentinfo","root","root");
             String sql = "INSERT INTO AdminLogin2 VALUES (?, ?)";
            PreparedStatement ps=con.prepareStatement(sql);
            ps.setString(1, UserName);
            ps.setString(2, Password);

            if(Username.getText().equals(UserName) && (pass.getText().equals(Password))){

                JOptionPane.showMessageDialog(null, "Login Succesfully");
                
                    
                    Admin ob=new Admin();
                    ob.setVisible(true);
                    this.dispose();

                    }
                    else
                    {
                        System.out.println("Data not Found");
                    }

                }
                catch(Exception e){

                }

            
             if(Plk.getText().equals("")){

                JOptionPane.showMessageDialog(null, "Please Enter Captcha");

            }
            //       else if(Username.getText().equals("Palak")&& pass.getText().equals("12345")){
                //           JOptionPane.showMessageDialog(null, "Login Successfully");

                else{
                    JOptionPane.showMessageDialog(null, "WRONG USERNAME AND PASSWORD");

                    Username.setText("");
                    pass.setText("");
                }
            
          
    }//GEN-LAST:event_jButton1ActionPerformed
public   void generateRandomText() {
        String characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
        StringBuilder captchaText = new StringBuilder();

        for (int i = 0; i < 6; i++) { 
            int index = (int) (Math.random() * characters.length());
            captchaText.append(characters.charAt(index));
        }
        Plk.setText(captchaText.toString());
        
    }
   
